import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";gsap.registerPlugin(ScrollTrigger);const section3TL=gsap.timeline();section2TL.from("#section-3-first img",{duration:.5,alpha:0,rotation:270}).from("#section-3-first h1",{duration:.5,alpha:0,x:-300}).from("#section-3-first h4",{duration:.5,alpha:0}).from("#section-3-middle",{duration:.5,alpha:0}).from("#section-3-last",{duration:.5,alpha:0});export function section3Animation(){ScrollTrigger.create({markers:!0,animation:section2TL,toggleActions:"play none none none",trigger:"#section-3",start:"top, 25%",end:"bottom 75%"})}