import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";gsap.registerPlugin(ScrollTrigger);const section3TL=gsap.timeline();section3TL.from("h1",{duration:.5,alpha:0,rotation:180}).from("#section-3-right",{duration:.5,alpha:0,x:-300}).from("#section-3-first h4",{duration:.5,alpha:0}).from("#section-3-middle",{duration:.5,alpha:0}).from("#section-3-last",{duration:.5,alpha:0});export function section3Animation(){ScrollTrigger.create({animation:section3TL,toggleActions:"play none none none",trigger:"#section-3",start:"top, 25%",end:"bottom 75%"})}